import React, { useState } from "react";
import styled from "styled-components";

/* ================= STYLES ================= */

const Page = styled.div`
  padding: 32px;
  background: #f4f6f8;
  min-height: 100vh;
`;

const Card = styled.div`
  background: white;
  border-radius: 12px;
  padding: 24px;
`;

const Header = styled.div`
  margin-bottom: 24px;
`;

const Greeting = styled.h2`
  margin: 0;
`;

const SubText = styled.p`
  margin: 6px 0 0;
  color: #666;
`;

const TopActions = styled.div`
  display: flex;
  gap: 12px;
  margin-top: 20px;
`;

const Button = styled.button`
  background: ${({ danger }) => (danger ? "#dc3545" : "#008080")};
  color: white;
  border: none;
  padding: 10px 18px;
  border-radius: 8px;
  cursor: pointer;
`;

const FiltersBox = styled.div`
  margin: 28px 0;
  padding: 16px;
  border-radius: 10px;
  background: #f1f3f5;
`;

const Filters = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 12px;
`;

const Input = styled.input`
  padding: 10px;
  border-radius: 6px;
  border: 1px solid #ccc;
`;

const ClearBtn = styled.button`
  margin-top: 12px;
  background: transparent;
  border: none;
  color: #008080;
  cursor: pointer;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const Th = styled.th`
  text-align: left;
  padding: 12px;
  background: #e9ecef;
`;

const Td = styled.td`
  padding: 12px;
  border-bottom: 1px solid #eee;
`;

const RowActions = styled.div`
  display: flex;
  gap: 8px;
`;

/* ================= MODAL ================= */

const Overlay = styled.div`
  position: fixed;
  inset: 0;
  background: rgba(0,0,0,0.45);
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Modal = styled.div`
  background: white;
  padding: 24px;
  border-radius: 12px;
  width: 380px;
`;

const ModalActions = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 10px;
  margin-top: 18px;
`;

/* ================= COMPONENT ================= */

export default function AdminStudents() {
  const [adminName] = useState("Sample Admin");

  /* ---- UI MOCK DATA ---- */
  const [students, setStudents] = useState([
    { id: 1, name: "Rahul", department: "CSE", year: 3, username: "22A91A05A0" },
    { id: 2, name: "Anjali", department: "ECE", year: 2, username: "22A91A04B1" },
    { id: 3, name: "Kiran", department: "MECH", year: 4, username: "21A91A03C2" },
  ]);

  const [filters, setFilters] = useState({
    name: "",
    department: "",
    year: "",
  });

  const emptyStudent = { id: null, name: "", department: "", year: "", username: "" };
  const [formData, setFormData] = useState(emptyStudent);

  const [showCreate, setShowCreate] = useState(false);
  const [showEdit, setShowEdit] = useState(false);

  /* ---- CREATE ---- */
  const createStudent = () => {
    setStudents([...students, { ...formData, id: Date.now() }]);
    setFormData(emptyStudent);
    setShowCreate(false);
  };

  /* ---- UPDATE ---- */
  const updateStudent = () => {
    setStudents(
      students.map((s) => (s.id === formData.id ? formData : s))
    );
    setFormData(emptyStudent);
    setShowEdit(false);
  };

  /* ---- DELETE ---- */
  const deleteStudent = (id) => {
    setStudents(students.filter((s) => s.id !== id));
  };

  /* ---- FILTER ---- */
  const filteredStudents = students.filter((s) =>
    (filters.name === "" || s.name.toLowerCase().includes(filters.name.toLowerCase())) &&
    (filters.department === "" || s.department.toLowerCase().includes(filters.department.toLowerCase())) &&
    (filters.year === "" || String(s.year) === filters.year)
  );

  return (
    <Page>
      <Card>
        <Header>
          <Greeting>Hello {adminName} ðŸ‘‹</Greeting>
          <SubText>Student Management Dashboard</SubText>

          <TopActions>
            <Button onClick={() => setShowCreate(true)}>Create Student</Button>
          </TopActions>
        </Header>

        {/* FILTER UI */}
        <FiltersBox>
          <Filters>
            <Input placeholder="Filter by name"
              value={filters.name}
              onChange={(e) => setFilters({ ...filters, name: e.target.value })} />
            <Input placeholder="Filter by department"
              value={filters.department}
              onChange={(e) => setFilters({ ...filters, department: e.target.value })} />
            <Input placeholder="Filter by year"
              value={filters.year}
              onChange={(e) => setFilters({ ...filters, year: e.target.value })} />
          </Filters>

          <ClearBtn onClick={() => setFilters({ name: "", department: "", year: "" })}>
            Clear filters
          </ClearBtn>
        </FiltersBox>

        {/* TABLE */}
        <Table>
          <thead>
            <tr>
              <Th>Name</Th>
              <Th>Department</Th>
              <Th>Year</Th>
              <Th>Roll No</Th>
              <Th>Actions</Th>
            </tr>
          </thead>
          <tbody>
            {filteredStudents.map((s) => (
              <tr key={s.id}>
                <Td>{s.name}</Td>
                <Td>{s.department}</Td>
                <Td>{s.year}</Td>
                <Td>{s.username}</Td>
                <Td>
                  <RowActions>
                    <Button onClick={() => { setFormData(s); setShowEdit(true); }}>
                      Edit
                    </Button>
                    <Button danger onClick={() => deleteStudent(s.id)}>
                      Delete
                    </Button>
                  </RowActions>
                </Td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Card>

      {/* CREATE MODAL */}
      {showCreate && (
        <Overlay>
          <Modal>
            <h3>Create Student</h3>
            <Input placeholder="Name" value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })} />
            <Input placeholder="Department" value={formData.department}
              onChange={(e) => setFormData({ ...formData, department: e.target.value })} />
            <Input placeholder="Year" value={formData.year}
              onChange={(e) => setFormData({ ...formData, year: e.target.value })} />
            <Input placeholder="Roll Number" value={formData.username}
              onChange={(e) => setFormData({ ...formData, username: e.target.value })} />
            <ModalActions>
              <Button onClick={() => setShowCreate(false)}>Cancel</Button>
              <Button onClick={createStudent}>Create</Button>
            </ModalActions>
          </Modal>
        </Overlay>
      )}

      {/* UPDATE MODAL */}
      {showEdit && (
        <Overlay>
          <Modal>
            <h3>Update Student</h3>
            <Input value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })} />
            <Input value={formData.department}
              onChange={(e) => setFormData({ ...formData, department: e.target.value })} />
            <Input value={formData.year}
              onChange={(e) => setFormData({ ...formData, year: e.target.value })} />
            <ModalActions>
              <Button onClick={() => setShowEdit(false)}>Cancel</Button>
              <Button onClick={updateStudent}>Update</Button>
            </ModalActions>
          </Modal>
        </Overlay>
      )}
    </Page>
  );
}
